function [D,x] = cheb(N)% Compute Chebyshev-Gauss-Lobatto points and associated differentiation% matrix.% Input: N gives N+1 points with the first and last points at 1 and -1% respectively% Outputs: D = (N+1)x(N+1) differentiation matrix, x = Chebyshev points    if N==0        D=0;         x=1;         return    end    x = cos(pi*(0:N)/N)';     c = [2; ones(N-1,1); 2].*(-1).^(0:N)';    X = ones(N+1,N+1);    for i=0:N        X(:,i+1) = x;    end    dX = X-X';                      D  = (c*(1./c)')./(dX+(eye(N+1)));      % off-diagonal entries    D  = D - diag(sum(D, 2));                 % diagonal entriesend